const calculateAge = require("../../modules/calculateAge"); 
const generateRandomId = require("../../modules/generateRandomId"); 
const separateAndCapitalize = require("../../modules/separateAndCapitalize"); 
const capitalizedFirstLetter = require("../../modules/capitalizedFirstLetter")

module.exports = ({name,dateBirth, purpose}) => {
    const {firstName,lastName} = separateAndCapitalize(name)

    return {
        id: generateRandomId(), 
        firstName,
        lastName,
        dateBirth,
        age: calculateAge(dateBirth),
        purpose: capitalizedFirstLetter(purpose), 
      }
}
    