import { capitalizedFirstLetter } from "../../modules/capitalizedFirstLetter.mjs";
import { calculateAge } from "../../modules/calculateAge.mjs";
import { generateRandomId } from "../../modules/generateRandomId.mjs";
import { separateAndCapitalize } from "../../modules/separateAndCapitalize.mjs";

export default ({name,dateBirth, purpose}) => {
    const {firstName,lastName} = separateAndCapitalize(name)

    return {
        id: generateRandomId(), 
        firstName,
        lastName,
        dateBirth,
        age: calculateAge(dateBirth),
        purpose: capitalizedFirstLetter(purpose), 
    }
}


  